 */
function enqueue_role_manager_scripts() {
    global $post;
    
    if ($post && (
        has_shortcode($post->post_content, 'department_role_manager') || 
        has_shortcode($post->post_content, 'department_role_table_manager')
    )) {
        // โหลด JavaScript ถ้ามีการลงทะเบียน
