function save_tdep_image($term_id) {
    if (isset($_POST['tdep_image_id']) && '' !== $_POST['tdep_image_id']) {
        $image = $_POST['tdep_image_id'];
        update_term_meta($term_id, 'tdep_image_id', $image);
