    add_action('wp_ajax_secure_pdf_preview', 'handle_secure_pdf_preview');
    add_action('wp_ajax_nopriv_secure_pdf_preview', 'handle_secure_pdf_preview');
    
    // สร้างไฟล์ assets ถ้าจำเป็น
