        'hide_empty' => false,
    ));
    
    $next_id = is_wp_error($terms) ? 1 : count($terms) + 1;
    $slug = 'clicense-' . $next_id;
    
    // Insert the term
    $term = wp_insert_term(
        $term_name,
        'clicense',
        array(
            'slug' => $slug,
        )
    );
    
    if (is_wp_error($term)) {
        wp_send_json_error(array('message' => $term->get_error_message()));
    } else {
        wp_send_json_success(array(
            'message' => 'เพิ่มสัญญาอนุญาตสำเร็จแล้ว',
            'term_id' => $term['term_id'],
            'term_name' => $term_name,
            'term_slug' => $slug,
        ));
    }
}
